pair A,B,C,D,X,Y,EE,F;

A = (0,0);

B=(1,0);

C = (0,0.8);

D=(1,0.8);

EE = (0.35,-0.3);

F = (0.8,1.1);

draw(EE--F);

draw(A--B);

draw(C--D);

dot(A);

dot(B);

dot(C);

dot(D);

dot(EE);

dot(F);

