Which is the graph of $y = |f(x)|$?

[asy]
unitsize(0.5 cm);

picture[] graf;
int i, n;

real func(real x) {
  real y;
  if (x >= -3 && x <= 0) {y = -2 - x;}
  if (x >= 0 && x <= 2) {y = sqrt(4 - (x - 2)^2) - 2;}
  if (x >= 2 && x <= 3) {y = 2*(x - 2);}
  return(y);
}

real funca(real x) {
  return(func(abs(x)));
}

real funcb(real x) {
  real y = max(0,func(x));
  return(y);
}

real funcd(real x) {
  return(abs(func(x)));
}

real funce(real x) {
  return(abs(func(-x)));
}

for (n = 1; n <= 5; ++n) {
  graf[n] = new picture;
  for (i = -5; i <= 5; ++i) {
    draw(graf[n],(i,-5)--(i,5),gray(0.7));
    draw(graf[n],(-5,i)--(5,i),gray(0.7));
  }
  draw(graf[n],(-5,0)--(5,0),Arrows(6));
  draw(graf[n],(0,-5)--(0,5),Arrows(6));

  label(graf[n],"$x$", (5,0), E);
  label(graf[n],"$y$", (0,5), N);
}

